import { motion } from 'framer-motion'
import { BookOpen, Shield, Users, Zap, Eye, Heart, Download } from 'lucide-react'
import { useLanguage } from '../../context/LanguageContext'
import { useState, useEffect } from 'react'
import { statsApi, SystemStats } from '../../lib/firebase/stats'
import { clsx } from 'clsx'

export function About() {
    const { t } = useLanguage()
    const [stats, setStats] = useState<SystemStats>({ visitors: 0, contributors: 0, papersEngagement: 0 })

    useEffect(() => {
        statsApi.getStats().then(setStats)

        // Smart Visitor Tracking
        const trackVisit = async () => {
            const lastVisit = localStorage.getItem('last_visit_date')
            const today = new Date().toDateString()

            // Deduplicate: Only increment if they haven't visited today
            if (lastVisit !== today) {
                await statsApi.incrementVisitors()
                localStorage.setItem('last_visit_date', today)
            }
        }

        trackVisit()
    }, [])

    const featureIcons = [BookOpen, Zap, Shield, Users]
    const features = ((t('about.features') as unknown as any[]) || []).map((f, i) => ({
        ...f,
        icon: featureIcons[i] || BookOpen
    }))

    const statsConfig = [
        { label: 'Visitors', value: stats.visitors, icon: Eye, color: 'text-primary', bg: 'bg-primary/10' },
        { label: 'Contributors', value: stats.contributors, icon: Heart, color: 'text-rose-500', bg: 'bg-rose-50' },
        { label: 'Papers Engagement', value: stats.papersEngagement, icon: Download, color: 'text-amber-500', bg: 'bg-amber-50' },
    ]

    return (
        <div className="section-container section-spacing space-y-16 md:space-y-24">
            {/* Hero Section */}
            <section className="text-center space-y-8 max-w-3xl mx-auto">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="space-y-4"
                >
                    <h1 className="text-4xl md:text-5xl font-bold text-foreground leading-tight">
                        {t('about.hero.title')} <br />
                        <span className="text-primary italic">{t('about.hero.highlight')}</span>
                    </h1>
                    <p className="text-xl text-muted-foreground leading-relaxed">
                        {t('about.hero.description')}
                    </p>
                </motion.div>
            </section>

            {/* Statistics Section */}
            <section className="grid grid-cols-1 md:grid-cols-3 gap-8">
                {statsConfig.map((stat, index) => (
                    <motion.div
                        key={stat.label}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="bg-card border-none shadow-xl shadow-black/5 rounded-[2.5rem] p-8 text-center space-y-4 hover:scale-[1.02] transition-transform"
                    >
                        <div className={clsx("mx-auto p-4 rounded-2xl w-fit", stat.bg)}>
                            <stat.icon className={clsx("h-8 w-8", stat.color)} />
                        </div>
                        <div className="space-y-1">
                            <h4 className="text-4xl font-bold text-foreground">{stat.value.toLocaleString()}</h4>
                            <p className="text-sm font-bold uppercase tracking-widest text-muted-foreground">{stat.label}</p>
                        </div>
                    </motion.div>
                ))}
            </section>

            {/* Features Grid */}
            <section className="grid grid-cols-1 md:grid-cols-2 gap-12">
                {features.map((feature, index) => (
                    <motion.div
                        key={feature.title}
                        initial={{ opacity: 0, x: index % 2 === 0 ? -20 : 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="flex gap-6 p-8 rounded-[2.5rem] bg-muted/30 border border-transparent hover:border-primary/20 hover:bg-muted/50 transition-all duration-500 group"
                    >
                        <div className="shrink-0">
                            <div className="p-5 bg-primary/10 rounded-2xl group-hover:bg-primary group-hover:text-primary-foreground transition-all duration-500 shadow-inner">
                                <feature.icon className="h-8 w-8 text-primary group-hover:text-primary-foreground transition-colors" />
                            </div>
                        </div>
                        <div className="space-y-3">
                            <h3 className="text-2xl font-bold text-foreground">{feature.title}</h3>
                            <p className="text-muted-foreground leading-relaxed font-medium">
                                {feature.description}
                            </p>
                        </div>
                    </motion.div>
                ))}
            </section>

            {/* Mission Section */}
            <section className="rounded-[3rem] bg-secondary text-secondary-foreground p-12 md:p-20 text-center space-y-8 relative overflow-hidden shadow-2xl shadow-secondary/20">
                <div className="absolute top-0 right-0 w-1/3 h-full bg-white/5 skew-x-12 -z-0" />
                <div className="relative z-10 space-y-8">
                    <h2 className="text-4xl font-bold tracking-tight">{t('about.mission.title')}</h2>
                    <p className="text-xl text-secondary-foreground/80 max-w-2xl mx-auto leading-relaxed font-medium">
                        {t('about.mission.description')}
                    </p>
                </div>
            </section>
        </div>
    )
}

import { useState, useEffect } from 'react'
import { loadStripe } from '@stripe/stripe-js'
import { Elements } from '@stripe/react-stripe-js'
import { CreditCard } from 'lucide-react'
import { generateReceipt } from '../../lib/receipts'
import { configsApi } from '../../lib/firebase/configs'
import { CheckoutForm } from '../donation/CheckoutForm'
import { DonationPitch } from '../donation/DonationPitch'
import { DonationSuccess } from '../donation/DonationSuccess'

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY || 'pk_test_TYooMQauvdEDq54NiTphI7jx')

export function Contribute() {
    const [coffeePrice, setCoffeePrice] = useState(5)
    const [selectedDetail, setSelectedDetail] = useState(3)
    const [customAmount, setCustomAmount] = useState('')
    const [successData, setSuccessData] = useState<any | null>(null)

    useEffect(() => {
        const fetchDefaults = async () => {
            const defaults = await configsApi.getDonationSettings()
            if (defaults.coffeePrice) setCoffeePrice(defaults.coffeePrice)
        }
        fetchDefaults()
    }, [])

    const handleSuccess = (data: any) => {
        setSuccessData(data)
        generateReceipt(data)
    }

    const currentAmount = customAmount ? parseFloat(customAmount) : (selectedDetail * coffeePrice)

    if (successData) {
        return <DonationSuccess successData={successData} />
    }

    return (
        <div className="section-container page-header-padding pb-20">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-start">
                <DonationPitch
                    coffeePrice={coffeePrice}
                    selectedDetail={selectedDetail}
                    setSelectedDetail={setSelectedDetail}
                    customAmount={customAmount}
                    setCustomAmount={setCustomAmount}
                />

                {/* Right Side: Payment Form */}
                <div className="bg-white rounded-[2.5rem] shadow-xl border border-muted/50 p-8 lg:p-10 relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-64 h-64 bg-primary/5 rounded-full blur-3xl -mr-20 -mt-20 pointer-events-none" />

                    <div className="mb-8 flex items-center justify-between">
                        <h3 className="text-2xl font-bold text-secondary flex items-center gap-3">
                            <CreditCard className="text-primary" />
                            Payment Details
                        </h3>
                        <div className="text-right">
                            <p className="text-xs font-bold uppercase tracking-widest text-muted-foreground">Total to pay</p>
                            <p className="text-2xl font-bold text-secondary">${currentAmount.toFixed(2)}</p>
                        </div>
                    </div>

                    <Elements stripe={stripePromise} options={{
                        appearance: {
                            theme: 'stripe',
                            variables: {
                                colorPrimary: '#3b82f6',
                                borderRadius: '12px',
                                fontFamily: 'Inter, system-ui, sans-serif'
                            }
                        }
                    }}>
                        <CheckoutForm
                            amount={currentAmount}
                            coffeeCount={selectedDetail || 1}
                            onSuccess={handleSuccess}
                        />
                    </Elements>
                </div>
            </div>
        </div>
    )
}

import React, { createContext, useContext, useState, useEffect } from 'react';
import { onAuthStateChanged, User } from 'firebase/auth';
import { auth } from '../lib/firebase';
import { usersApi } from '../lib/firebase/users';
import { UserProfile } from '../lib/firebase/schema';

interface AuthContextType {
    user: User | null;
    profile: UserProfile | null;
    loading: boolean;
    isAdmin: boolean;
    isSuperAdmin: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
    const [user, setUser] = useState<User | null>(null);
    const [profile, setProfile] = useState<UserProfile | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (u) => {
            setUser(u);
            if (u) {
                // Fetch profile to get roles
                try {
                    const p = await usersApi.syncUser(u.uid, u.email || '', u.displayName || 'User');
                    setProfile(p);
                } catch (error) {
                    console.error('Error syncing user:', error);
                }
            } else {
                setProfile(null);
            }
            setLoading(false);
        });
        return () => unsubscribe();
    }, []);

    const value = {
        user,
        profile,
        loading,
        isAdmin: profile?.role === 'admin' || profile?.role === 'super-admin',
        isSuperAdmin: profile?.role === 'super-admin'
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
}

import React, { createContext, useContext, useState, useEffect } from 'react'
import { configsApi, FilterConfig } from '../lib/firebase/configs'

interface FilterContextType {
    filters: FilterConfig | null
    loading: boolean
}

const FilterContext = createContext<FilterContextType | undefined>(undefined)

export function FilterProvider({ children }: { children: React.ReactNode }) {
    const [filters, setFilters] = useState<FilterConfig | null>(null)
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        const unsubscribe = configsApi.subscribeFilters((data) => {
            const currentYear = new Date().getFullYear()
            // Legacy logic: All years since 2020, plus next year if it's near
            const generatedYears = Array.from(
                { length: currentYear - 2020 + 2 },
                (_, i) => (currentYear + 1 - i).toString()
            )

            setFilters({
                subjects: data.subjects || [],
                categories: data.categories || [],
                parts: data.parts || [],
                languages: data.languages || [],
                // Merge DB years with generated ones, ensuring uniqueness
                years: Array.from(new Set([...generatedYears, ...(data.years || [])]))
                    .sort((a, b) => parseInt(b) - parseInt(a))
            })
            setLoading(false)
        })
        return () => unsubscribe()
    }, [])

    return (
        <FilterContext.Provider value={{ filters, loading }}>
            {children}
        </FilterContext.Provider>
    )
}

export function useFilters() {
    const context = useContext(FilterContext)
    if (context === undefined) {
        throw new Error('useFilters must be used within a FilterProvider')
    }
    return context
}

import React, { createContext, useContext, useState, useEffect } from 'react';
import { translations } from '../lib/translations.ts';

type Language = 'en' | 'ta';

interface LanguageContextType {
    language: Language;
    setLanguage: (lang: Language) => void;
    t: (path: string) => any;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

export function LanguageProvider({ children }: { children: React.ReactNode }) {
    const [language, setLanguage] = useState<Language>(() => {
        const saved = localStorage.getItem('language');
        return (saved === 'en' || saved === 'ta') ? saved : 'en';
    });

    useEffect(() => {
        localStorage.setItem('language', language);
        document.documentElement.lang = language;
    }, [language]);

    const t = (path: string) => {
        const keys = path.split('.');
        let current: any = translations[language];

        for (const key of keys) {
            if (current && current[key] !== undefined) {
                current = current[key];
            } else {
                return path; // Fallback to path if not found
            }
        }
        return current;
    };

    return (
        <LanguageContext.Provider value={{ language, setLanguage, t }}>
            {children}
        </LanguageContext.Provider>
    );
}

export function useLanguage() {
    const context = useContext(LanguageContext);
    if (context === undefined) {
        throw new Error('useLanguage must be used within a LanguageProvider');
    }
    return context;
}

import { db } from '../firebase'
import { doc, getDoc, setDoc, onSnapshot } from 'firebase/firestore'

export interface FilterConfig {
    subjects: string[]
    categories: string[]
    parts: string[]
    languages: string[]
    years: string[]
}

export interface SocialConfig {
    twitter?: string
    facebook?: string
    instagram?: string
}

export interface DonationConfig {
    coffeePrice?: number
    enabled?: boolean
}

export const configsApi = {
    async getFilters(): Promise<FilterConfig> {
        const docRef = doc(db, 'configs', 'filters')
        const snap = await getDoc(docRef)
        if (snap.exists()) {
            return snap.data() as FilterConfig
        }
        return { subjects: [], categories: [], parts: [], languages: [], years: [] }
    },

    async getAdminAuth(): Promise<{ email?: string, password?: string }> {
        const docRef = doc(db, 'configs', 'admin_auth')
        const snap = await getDoc(docRef)
        return snap.exists() ? snap.data() as any : {}
    },

    subscribeFilters(callback: (filters: FilterConfig) => void) {
        return onSnapshot(doc(db, 'configs', 'filters'), (snap) => {
            if (snap.exists()) {
                callback(snap.data() as FilterConfig)
            } else {
                callback({ subjects: [], categories: [], parts: [], languages: [], years: [] })
            }
        })
    },

    async updateFilters(filters: FilterConfig) {
        await setDoc(doc(db, 'configs', 'filters'), filters)
    },

    async getSocials(): Promise<SocialConfig> {
        const docRef = doc(db, 'configs', 'socials')
        const snap = await getDoc(docRef)
        return snap.exists() ? snap.data() as SocialConfig : {}
    },

    async updateSocials(socials: SocialConfig) {
        await setDoc(doc(db, 'configs', 'socials'), socials)
    },

    subscribeSocials(callback: (socials: SocialConfig) => void) {
        return onSnapshot(doc(db, 'configs', 'socials'), (snap) => {
            if (snap.exists()) {
                callback(snap.data() as SocialConfig)
            } else {
                callback({})
            }
        })
    },

    async getDonationSettings(): Promise<DonationConfig> {
        const docRef = doc(db, 'configs', 'donations')
        const snap = await getDoc(docRef)
        return snap.exists() ? snap.data() as DonationConfig : { coffeePrice: 5, enabled: true }
    },

    async updateDonationSettings(settings: DonationConfig) {
        await setDoc(doc(db, 'configs', 'donations'), settings)
    }
}

// Firestore Schema Definitions

export interface Paper {
    id?: string;
    title: string;
    subject: string;
    year: number;
    examType: string; // 'Final', 'Midterm', etc.
    part: string;
    language: string;
    fileUrl: string;

    // Search Optimization
    keywords: string[]; // ['m', 'ma', 'math', '2023']

    // Denormalized Metadata for UI efficiency
    metadata: {
        uploadedBy: string; // User UID
        uploaderName: string; // Display Name
        fileSize?: number;
        mimeType?: string;
        originalName?: string;
    };

    createdAt: any; // Firestore Timestamp
    updatedAt?: any;
}

export interface UserProfile {
    uid: string;
    email: string;
    displayName: string;
    role: 'admin' | 'user' | 'super-admin';
    blocked?: boolean;
    photoURL?: string;
    createdAt: any;
}

export interface Contribution {
